import { useState } from 'react';

function Square({value, onSquareClick, isWinningSquare}) {
  const className = isWinningSquare ? 'square winning' : 'square';
  return (
  <button className={className} onClick={onSquareClick}>
    {value}
  </button>
  );
}


function Board( {xIsNext, squares, onPlay, winningSquares}) {
  const winner = calculateWinner(squares);
  let status;
  if (winner) {
    status = 'Winner: ' + winner;
  }
  else {
    status = 'Next player: ' + (xIsNext ? 'X' : 'O');
  }

  function handleClick(i) {
    if (squares[i] || calculateWinner(squares)) {
      return;
    }
    const newSquares = squares.slice();
    if (xIsNext) {
      newSquares[i] = 'X';
    }
    else {
      newSquares[i] = 'O';
    }
    onPlay(newSquares);
  }

  return (
    <>
    <div className="status">{status}</div>
    <div className="board">
      {[0, 1, 2, 3].map(row => (
        <div key={row} className="board-row">
          {[0, 1, 2, 3].map(col => (
            <Square key={row * 4 + col} value={squares[row * 4 + col]} onSquareClick={() => handleClick(row * 4 + col)} isWinningSquare={winningSquares.includes(row * 4 + col)}/>
          ))}
        </div>
      ))}
    </div>
  </>
  );  

  function calculateWinner(squares) {
    const lines = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6],
    ];
    for (let i = 0; i < lines.length; i++) {
      const [a, b, c] = lines[i];
      if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
        return squares[a];
      }
    }
    return null;
  }

}

export default function Game() {
  const [history, setHistory] = useState([Array(9).fill(null)]);
  const [currentMove, setCurrentMove] = useState(0);
  const xIsNext = (currentMove % 2) === 0;
  const currentSquare = history[currentMove];

  function handlePlay(nextSquares){
    const nextHistory = [...history.slice(0, currentMove + 1), nextSquares];
    setHistory(nextHistory);
    setCurrentMove(nextHistory.length - 1);
  }

  function jumpTo(nextMove) {
    setCurrentMove(nextMove);
  }

  const moves = history.map((squares, move) => {
    let description;
    if (move > 0) {
      description = '指し手 #' + move + 'に戻る';
    } else {
      description = 'ゲームのリセット';
    }
    return (
      <li key={move}>
        <button onClick={() => jumpTo(move)}>{description}</button>
      </li>
    );
  });

  return (
    <div className="game">
      <div className="game-board">
        <Board xIsNext={xIsNext} squares={currentSquare} onPlay={handlePlay} />
      </div>
      <div className="game-info">
        <ol>{moves}</ol>
      </div>
   </div>
  );
}
